/**
 * Command Palette Component
 *
 * Keyboard-driven command palette for quick actions (Cmd+K / Ctrl+K).
 *
 * Phase 4B: UX Polish - Command Palette
 */

import React, { useState, useEffect, useCallback } from 'react';
import {
  Search,
  TrendingUp,
  BarChart3,
  Brain,
  Settings,
  Newspaper,
  Target,
  Activity,
  Database,
  MessageSquare,
  CreditCard,
} from 'lucide-react';

interface Command {
  id: string;
  label: string;
  description: string;
  icon: React.ElementType;
  action: () => void;
  keywords?: string[];
}

interface CommandPaletteProps {
  onNavigate: (section: string) => void;
}

const CommandPalette: React.FC<CommandPaletteProps> = ({ onNavigate }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [search, setSearch] = useState('');
  const [selectedIndex, setSelectedIndex] = useState(0);

  const commands: Command[] = [
    {
      id: 'positions',
      label: 'View Positions',
      description: 'See active trading positions',
      icon: TrendingUp,
      action: () => onNavigate('positions'),
      keywords: ['trade', 'portfolio', 'holdings'],
    },
    {
      id: 'execute',
      label: 'Execute Trade',
      description: 'Place a new order',
      icon: Target,
      action: () => onNavigate('execute'),
      keywords: ['buy', 'sell', 'order', 'trade'],
    },
    {
      id: 'analytics',
      label: 'Analytics Dashboard',
      description: 'View P&L and performance',
      icon: BarChart3,
      action: () => onNavigate('analytics'),
      keywords: ['pnl', 'profit', 'loss', 'performance', 'metrics'],
    },
    {
      id: 'ml-training',
      label: 'ML Training',
      description: 'Train machine learning models',
      icon: Brain,
      action: () => onNavigate('ml-training'),
      keywords: ['ai', 'machine learning', 'model', 'train'],
    },
    {
      id: 'ml-analytics',
      label: 'ML Analytics',
      description: 'View ML model performance',
      icon: Activity,
      action: () => onNavigate('ml-analytics'),
      keywords: ['ai', 'accuracy', 'predictions'],
    },
    {
      id: 'portfolio-optimizer',
      label: 'Portfolio Optimizer',
      description: 'Optimize portfolio allocation',
      icon: Target,
      action: () => onNavigate('portfolio-optimizer'),
      keywords: ['optimize', 'allocation', 'rebalance'],
    },
    {
      id: 'news',
      label: 'News Sentiment',
      description: 'Analyze market sentiment',
      icon: Newspaper,
      action: () => onNavigate('news'),
      keywords: ['sentiment', 'headlines', 'analysis'],
    },
    {
      id: 'ai-chat',
      label: 'AI Chat',
      description: 'Chat with Claude AI',
      icon: MessageSquare,
      action: () => onNavigate('ai-chat'),
      keywords: ['claude', 'assistant', 'help', 'chat'],
    },
    {
      id: 'subscription',
      label: 'Subscription & Billing',
      description: 'Manage subscription',
      icon: CreditCard,
      action: () => onNavigate('subscription'),
      keywords: ['billing', 'payment', 'upgrade', 'plan'],
    },
    {
      id: 'settings',
      label: 'Settings',
      description: 'Configure application',
      icon: Settings,
      action: () => onNavigate('settings'),
      keywords: ['preferences', 'config', 'options'],
    },
  ];

  const filteredCommands = commands.filter((cmd) => {
    const searchLower = search.toLowerCase();
    return (
      cmd.label.toLowerCase().includes(searchLower) ||
      cmd.description.toLowerCase().includes(searchLower) ||
      cmd.keywords?.some((k) => k.includes(searchLower))
    );
  });

  const handleKeyDown = useCallback(
    (e: KeyboardEvent) => {
      // Open with Cmd+K or Ctrl+K
      if ((e.metaKey || e.ctrlKey) && e.key === 'k') {
        e.preventDefault();
        setIsOpen((prev) => !prev);
        setSearch('');
        setSelectedIndex(0);
      }

      // Close with Escape
      if (e.key === 'Escape') {
        setIsOpen(false);
      }

      if (!isOpen) return;

      // Navigate with arrow keys
      if (e.key === 'ArrowDown') {
        e.preventDefault();
        setSelectedIndex((prev) => Math.min(prev + 1, filteredCommands.length - 1));
      }

      if (e.key === 'ArrowUp') {
        e.preventDefault();
        setSelectedIndex((prev) => Math.max(prev - 1, 0));
      }

      // Execute with Enter
      if (e.key === 'Enter' && filteredCommands[selectedIndex]) {
        e.preventDefault();
        filteredCommands[selectedIndex].action();
        setIsOpen(false);
        setSearch('');
      }
    },
    [isOpen, filteredCommands, selectedIndex]
  );

  useEffect(() => {
    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [handleKeyDown]);

  useEffect(() => {
    // Reset selection when search changes
    setSelectedIndex(0);
  }, [search]);

  if (!isOpen) return null;

  return (
    <>
      {/* Backdrop */}
      <div
        style={{
          position: 'fixed',
          inset: 0,
          zIndex: 9999,
          background: 'rgba(0, 0, 0, 0.7)',
          backdropFilter: 'blur(4px)',
        }}
        onClick={() => setIsOpen(false)}
      />

      {/* Command Palette */}
      <div
        style={{
          position: 'fixed',
          top: '20%',
          left: '50%',
          transform: 'translateX(-50%)',
          zIndex: 10000,
          width: '90%',
          maxWidth: '600px',
          background: 'rgba(15, 23, 42, 0.95)',
          border: '1px solid rgba(71, 85, 105, 0.5)',
          borderRadius: '16px',
          boxShadow: '0 24px 48px rgba(0, 0, 0, 0.5)',
          backdropFilter: 'blur(20px)',
          overflow: 'hidden',
        }}
      >
        {/* Search Input */}
        <div
          style={{
            padding: '20px',
            borderBottom: '1px solid rgba(71, 85, 105, 0.3)',
            display: 'flex',
            alignItems: 'center',
            gap: '12px',
          }}
        >
          <Search size={20} color="#94a3b8" />
          <input
            type="text"
            placeholder="Search commands..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            autoFocus
            style={{
              flex: 1,
              background: 'transparent',
              border: 'none',
              outline: 'none',
              color: '#fff',
              fontSize: '16px',
              fontFamily: 'inherit',
            }}
          />
          <kbd
            style={{
              padding: '4px 8px',
              background: 'rgba(51, 65, 85, 0.6)',
              borderRadius: '4px',
              fontSize: '12px',
              color: '#94a3b8',
              border: '1px solid rgba(71, 85, 105, 0.3)',
            }}
          >
            ESC
          </kbd>
        </div>

        {/* Command List */}
        <div
          style={{
            maxHeight: '400px',
            overflowY: 'auto',
            padding: '8px',
          }}
        >
          {filteredCommands.length === 0 ? (
            <div
              style={{
                padding: '40px',
                textAlign: 'center',
                color: '#64748b',
                fontSize: '14px',
              }}
            >
              No commands found
            </div>
          ) : (
            filteredCommands.map((cmd, idx) => {
              const Icon = cmd.icon;
              const isSelected = idx === selectedIndex;

              return (
                <div
                  key={cmd.id}
                  onClick={() => {
                    cmd.action();
                    setIsOpen(false);
                    setSearch('');
                  }}
                  onMouseEnter={() => setSelectedIndex(idx)}
                  style={{
                    padding: '12px 16px',
                    borderRadius: '8px',
                    background: isSelected
                      ? 'rgba(16, 185, 129, 0.15)'
                      : 'transparent',
                    border: isSelected
                      ? '1px solid rgba(16, 185, 129, 0.3)'
                      : '1px solid transparent',
                    cursor: 'pointer',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '12px',
                    marginBottom: '4px',
                    transition: 'all 0.15s ease',
                  }}
                >
                  <div
                    style={{
                      width: '40px',
                      height: '40px',
                      borderRadius: '8px',
                      background: isSelected
                        ? 'rgba(16, 185, 129, 0.2)'
                        : 'rgba(51, 65, 85, 0.4)',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                    }}
                  >
                    <Icon
                      size={20}
                      color={isSelected ? '#10b981' : '#94a3b8'}
                    />
                  </div>
                  <div style={{ flex: 1 }}>
                    <div
                      style={{
                        fontSize: '14px',
                        fontWeight: 600,
                        color: isSelected ? '#10b981' : '#fff',
                        marginBottom: '2px',
                      }}
                    >
                      {cmd.label}
                    </div>
                    <div
                      style={{
                        fontSize: '12px',
                        color: '#94a3b8',
                      }}
                    >
                      {cmd.description}
                    </div>
                  </div>
                  {isSelected && (
                    <kbd
                      style={{
                        padding: '4px 8px',
                        background: 'rgba(16, 185, 129, 0.2)',
                        borderRadius: '4px',
                        fontSize: '11px',
                        color: '#10b981',
                        border: '1px solid rgba(16, 185, 129, 0.3)',
                      }}
                    >
                      ‚Üµ
                    </kbd>
                  )}
                </div>
              );
            })
          )}
        </div>

        {/* Footer */}
        <div
          style={{
            padding: '12px 20px',
            borderTop: '1px solid rgba(71, 85, 105, 0.3)',
            display: 'flex',
            gap: '16px',
            fontSize: '12px',
            color: '#64748b',
          }}
        >
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <kbd
              style={{
                padding: '2px 6px',
                background: 'rgba(51, 65, 85, 0.4)',
                borderRadius: '3px',
                fontSize: '11px',
                border: '1px solid rgba(71, 85, 105, 0.3)',
              }}
            >
              ‚Üë‚Üì
            </kbd>
            Navigate
          </div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <kbd
              style={{
                padding: '2px 6px',
                background: 'rgba(51, 65, 85, 0.4)',
                borderRadius: '3px',
                fontSize: '11px',
                border: '1px solid rgba(71, 85, 105, 0.3)',
              }}
            >
              ‚Üµ
            </kbd>
            Select
          </div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
            <kbd
              style={{
                padding: '2px 6px',
                background: 'rgba(51, 65, 85, 0.4)',
                borderRadius: '3px',
                fontSize: '11px',
                border: '1px solid rgba(71, 85, 105, 0.3)',
              }}
            >
              ESC
            </kbd>
            Close
          </div>
        </div>
      </div>
    </>
  );
};

export default CommandPalette;

"use client";

import { useState, useEffect } from "react";
import { Card, Button } from "./ui";
import { theme } from "../styles/theme";
import { showError, showSuccess } from "../lib/toast";
import { Database, RefreshCw, CheckCircle, AlertTriangle, TrendingUp, Calendar, Activity } from "lucide-react";
import { useIsMobile } from "../hooks/useBreakpoint";

interface ModelMetrics {
  model_id: string;
  model_type: string;
  version: string;
  accuracy: number;
  samples_trained: number;
  last_trained: string;
  training_duration_seconds: number;
  status: "active" | "training" | "stale" | "error";
  features_count: number;
  hyperparameters: Record<string, any>;
}

interface ModelHealth {
  model_id: string;
  health_score: number;
  prediction_accuracy: number;
  days_since_training: number;
  total_predictions: number;
  requires_retraining: boolean;
  issues: string[];
  recommendations: string[];
}

interface ModelManagementData {
  models: ModelMetrics[];
  health_checks: ModelHealth[];
  next_scheduled_training: string | null;
  auto_retrain_enabled: boolean;
  system_status: "healthy" | "degraded" | "critical";
}

export default function MLModelManagement() {
  const isMobile = useIsMobile();
  const [data, setData] = useState<ModelManagementData | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [retrainingModel, setRetrainingModel] = useState<string | null>(null);

  useEffect(() => {
    loadModelStatus();
  }, []);

  const loadModelStatus = async () => {
    setIsLoading(true);
    try {
      const res = await fetch("/api/proxy/api/ml/models/status");
      if (!res.ok) throw new Error("Failed to load model status");
      const modelData = await res.json();
      setData(modelData);
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : "Unknown error";
      showError(`Failed to load models: ${errorMessage}`);
    } finally {
      setIsLoading(false);
    }
  };

  const retrainModel = async (modelId: string) => {
    setRetrainingModel(modelId);
    try {
      const res = await fetch(`/api/proxy/api/ml/models/${modelId}/retrain`, {
        method: "POST",
      });
      if (!res.ok) throw new Error("Retraining failed");
      const result = await res.json();
      showSuccess(`Model ${modelId} retrained successfully!`);
      await loadModelStatus(); // Refresh data
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : "Unknown error";
      showError(`Retraining failed: ${errorMessage}`);
    } finally {
      setRetrainingModel(null);
    }
  };

  const toggleAutoRetrain = async () => {
    try {
      const newState = !data?.auto_retrain_enabled;
      const res = await fetch("/api/proxy/api/ml/models/auto-retrain", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ enabled: newState }),
      });
      if (!res.ok) throw new Error("Failed to toggle auto-retrain");
      showSuccess(`Auto-retrain ${newState ? "enabled" : "disabled"}`);
      await loadModelStatus();
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : "Unknown error";
      showError(`Failed to toggle auto-retrain: ${errorMessage}`);
    }
  };

  const getStatusColor = (status: string): string => {
    switch (status) {
      case "active":
      case "healthy":
        return theme.colors.success;
      case "training":
      case "degraded":
        return theme.colors.warning;
      case "stale":
      case "critical":
      case "error":
        return theme.colors.error;
      default:
        return theme.colors.textMuted;
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case "active":
      case "healthy":
        return <CheckCircle size={20} color={theme.colors.success} />;
      case "training":
        return <RefreshCw size={20} color={theme.colors.warning} className="animate-spin" />;
      case "stale":
      case "degraded":
      case "critical":
      case "error":
        return <AlertTriangle size={20} color={theme.colors.error} />;
      default:
        return <Activity size={20} />;
    }
  };

  const getHealthScoreColor = (score: number): string => {
    if (score >= 80) return theme.colors.success;
    if (score >= 60) return theme.colors.warning;
    return theme.colors.error;
  };

  const formatDuration = (seconds: number): string => {
    if (seconds < 60) return `${seconds}s`;
    if (seconds < 3600) return `${Math.floor(seconds / 60)}m`;
    return `${Math.floor(seconds / 3600)}h ${Math.floor((seconds % 3600) / 60)}m`;
  };

  const formatDate = (dateString: string): string => {
    const date = new Date(dateString);
    const now = new Date();
    const diffMs = now.getTime() - date.getTime();
    const diffDays = Math.floor(diffMs / (1000 * 60 * 60 * 24));

    if (diffDays === 0) return "Today";
    if (diffDays === 1) return "Yesterday";
    if (diffDays < 7) return `${diffDays} days ago`;
    return date.toLocaleDateString();
  };

  return (
    <div style={{ padding: isMobile ? theme.spacing.md : theme.spacing.lg }}>
      {/* Header */}
      <div style={{ marginBottom: theme.spacing.lg }}>
        <h2
          style={{
            margin: 0,
            fontSize: isMobile ? "24px" : "32px",
            fontWeight: "700",
            color: theme.colors.text,
            textShadow: theme.glow.green,
            marginBottom: theme.spacing.xs,
            display: "flex",
            alignItems: "center",
            gap: theme.spacing.sm,
          }}
        >
          <Database size={32} color={theme.colors.primary} />
          ML Model Management
        </h2>
        <p
          style={{
            margin: 0,
            fontSize: "14px",
            color: theme.colors.textMuted,
          }}
        >
          Monitor model health, versions, and performance
        </p>
      </div>

      {/* Controls */}
      <Card glow="green" style={{ marginBottom: theme.spacing.lg }}>
        <div
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            flexWrap: "wrap",
            gap: theme.spacing.md,
          }}
        >
          <Button onClick={loadModelStatus} loading={isLoading} variant="primary">
            <RefreshCw size={18} />
            Refresh Status
          </Button>

          {data && (
            <label
              style={{
                display: "flex",
                alignItems: "center",
                gap: theme.spacing.sm,
                fontSize: "14px",
                color: theme.colors.text,
                cursor: "pointer",
              }}
            >
              <input
                type="checkbox"
                checked={data.auto_retrain_enabled}
                onChange={toggleAutoRetrain}
                style={{ cursor: "pointer" }}
              />
              <Activity size={16} />
              Auto-retrain enabled
            </label>
          )}

          {data?.next_scheduled_training && (
            <div
              style={{
                fontSize: "13px",
                color: theme.colors.textMuted,
              }}
            >
              <Calendar size={14} style={{ display: "inline", marginRight: "4px" }} />
              Next training: {formatDate(data.next_scheduled_training)}
            </div>
          )}
        </div>
      </Card>

      {/* System Status */}
      {data && (
        <Card
          glow={data.system_status === "healthy" ? "green" : data.system_status === "degraded" ? "yellow" : "red"}
          style={{ marginBottom: theme.spacing.lg }}
        >
          <div
            style={{
              display: "flex",
              alignItems: "center",
              gap: theme.spacing.md,
            }}
          >
            {getStatusIcon(data.system_status)}
            <div>
              <h3
                style={{
                  margin: 0,
                  fontSize: "18px",
                  fontWeight: "600",
                  color: getStatusColor(data.system_status),
                  textTransform: "uppercase",
                }}
              >
                System Status: {data.system_status}
              </h3>
              <p
                style={{
                  margin: 0,
                  fontSize: "13px",
                  color: theme.colors.textMuted,
                  marginTop: theme.spacing.xs,
                }}
              >
                {data.models.length} models active ‚Ä¢{" "}
                {data.health_checks.filter((h) => h.requires_retraining).length} require retraining
              </p>
            </div>
          </div>
        </Card>
      )}

      {/* Models Grid */}
      {data && (
        <div
          style={{
            display: "grid",
            gridTemplateColumns: isMobile ? "1fr" : "repeat(auto-fit, minmax(400px, 1fr))",
            gap: theme.spacing.lg,
          }}
        >
          {data.models.map((model, idx) => {
            const health = data.health_checks.find((h) => h.model_id === model.model_id);

            return (
              <Card key={idx} glow="cyan" style={{ position: "relative" }}>
                {/* Model Header */}
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "flex-start",
                    marginBottom: theme.spacing.md,
                  }}
                >
                  <div>
                    <h3
                      style={{
                        margin: 0,
                        fontSize: "20px",
                        fontWeight: "700",
                        color: theme.colors.text,
                      }}
                    >
                      {model.model_type.replace(/_/g, " ").toUpperCase()}
                    </h3>
                    <div
                      style={{
                        fontSize: "12px",
                        color: theme.colors.textMuted,
                        marginTop: theme.spacing.xs,
                      }}
                    >
                      v{model.version} ‚Ä¢ {model.model_id}
                    </div>
                  </div>

                  <div
                    style={{
                      display: "flex",
                      alignItems: "center",
                      gap: theme.spacing.xs,
                      padding: "6px 12px",
                      background: `rgba(${model.status === "active" ? "16, 185, 129" : model.status === "training" ? "251, 191, 36" : "239, 68, 68"}, 0.2)`,
                      border: `1px solid ${getStatusColor(model.status)}`,
                      borderRadius: theme.borderRadius.sm,
                    }}
                  >
                    {getStatusIcon(model.status)}
                    <span
                      style={{
                        fontSize: "12px",
                        fontWeight: "700",
                        color: getStatusColor(model.status),
                        textTransform: "uppercase",
                      }}
                    >
                      {model.status}
                    </span>
                  </div>
                </div>

                {/* Model Metrics */}
                <div
                  style={{
                    display: "grid",
                    gridTemplateColumns: "repeat(2, 1fr)",
                    gap: theme.spacing.sm,
                    marginBottom: theme.spacing.md,
                  }}
                >
                  <div
                    style={{
                      padding: theme.spacing.sm,
                      background: "rgba(6, 182, 212, 0.1)",
                      borderRadius: theme.borderRadius.sm,
                    }}
                  >
                    <div style={{ fontSize: "11px", color: theme.colors.textMuted }}>ACCURACY</div>
                    <div
                      style={{
                        fontSize: "24px",
                        fontWeight: "700",
                        color: theme.colors.secondary,
                      }}
                    >
                      {(model.accuracy * 100).toFixed(1)}%
                    </div>
                  </div>

                  <div
                    style={{
                      padding: theme.spacing.sm,
                      background: "rgba(139, 92, 246, 0.1)",
                      borderRadius: theme.borderRadius.sm,
                    }}
                  >
                    <div style={{ fontSize: "11px", color: theme.colors.textMuted }}>SAMPLES</div>
                    <div
                      style={{
                        fontSize: "24px",
                        fontWeight: "700",
                        color: theme.colors.accent,
                      }}
                    >
                      {model.samples_trained.toLocaleString()}
                    </div>
                  </div>

                  <div
                    style={{
                      padding: theme.spacing.sm,
                      background: "rgba(16, 185, 129, 0.1)",
                      borderRadius: theme.borderRadius.sm,
                    }}
                  >
                    <div style={{ fontSize: "11px", color: theme.colors.textMuted }}>FEATURES</div>
                    <div
                      style={{
                        fontSize: "24px",
                        fontWeight: "700",
                        color: theme.colors.success,
                      }}
                    >
                      {model.features_count}
                    </div>
                  </div>

                  <div
                    style={{
                      padding: theme.spacing.sm,
                      background: "rgba(251, 191, 36, 0.1)",
                      borderRadius: theme.borderRadius.sm,
                    }}
                  >
                    <div style={{ fontSize: "11px", color: theme.colors.textMuted }}>TRAINING TIME</div>
                    <div
                      style={{
                        fontSize: "20px",
                        fontWeight: "700",
                        color: theme.colors.warning,
                      }}
                    >
                      {formatDuration(model.training_duration_seconds)}
                    </div>
                  </div>
                </div>

                {/* Health Info */}
                {health && (
                  <div
                    style={{
                      padding: theme.spacing.md,
                      background: "rgba(15, 23, 42, 0.5)",
                      borderRadius: theme.borderRadius.md,
                      marginBottom: theme.spacing.md,
                    }}
                  >
                    <div
                      style={{
                        display: "flex",
                        justifyContent: "space-between",
                        alignItems: "center",
                        marginBottom: theme.spacing.sm,
                      }}
                    >
                      <span style={{ fontSize: "13px", color: theme.colors.textMuted }}>Health Score</span>
                      <span
                        style={{
                          fontSize: "20px",
                          fontWeight: "700",
                          color: getHealthScoreColor(health.health_score),
                        }}
                      >
                        {health.health_score.toFixed(0)}%
                      </span>
                    </div>

                    <div
                      style={{
                        width: "100%",
                        height: "8px",
