// Helper function to fetch real market data
async function fetchLiveMarketData() {
  try {
    console.info("[MorningRoutine] üî¥ Fetching LIVE market data...");
    const scanner = await fetchUnder4Scanner();

    console.info("[MorningRoutine] ‚úÖ Received live data:", scanner);

    return {
      candidates: scanner.candidates || [],
      count: scanner.count || 0,
      timestamp: new Date().toISOString(),
    };
  } catch (error) {
    console.error("[MorningRoutine] ‚ùå Failed to fetch live market data:", error);
    return null;
  }
}

// Helper function to format live data into markdown
function formatLiveMarketData(
  data: {
    count: number;
    candidates: Array<{
      symbol: string;
      price: number;
      change: number;
      changePercent: number;
      volume: number;
      marketCap: number;
    }>;
