                    </div>
                  </button>
                ))}
              </div>
            </GlassCard>

            {/* Preview */}
            <GlassCard
              style={{ background: `${accentColor}10`, border: `1px solid ${accentColor}40` }}
            >
              <h3
                style={{
                  fontSize: "18px",
                  fontWeight: "600",
                  color: theme.colors.text,
                  marginBottom: theme.spacing.md,
                  display: "flex",
                  alignItems: "center",
                  gap: theme.spacing.sm,
                }}
              >
                <Bell style={{ width: "20px", height: "20px", color: accentColor }} />
                Morning Briefing Preview
              </h3>
              <div
                style={{
                  padding: theme.spacing.md,
                  background: theme.background.glass,
                  borderRadius: theme.borderRadius.md,
                }}
              >
                <p
                  style={{
                    color: theme.colors.text,
                    fontWeight: "600",
                    marginBottom: theme.spacing.sm,
                  }}
                >
                  Good morning! Here&apos;s your {scheduleTime} briefing:
                </p>
                <div
                  style={{
                    display: "flex",
                    flexDirection: "column",
                    gap: theme.spacing.xs,
                    color: theme.colors.textMuted,
                    fontSize: "14px",
                  }}
                >
                  {selectedSteps.map((stepId) => {
                    const step = availableSteps.find((s) => s.id === stepId);
                    return step ? (
                      <div key={stepId}>
                        {step.icon} {step.label}
                      </div>
                    ) : null;
                  })}
                </div>
              </div>
            </GlassCard>

            {/* Save Button */}
            <GlassButton onClick={handleSaveSchedule}>
              <CheckCircle style={{ width: "18px", height: "18px" }} />
              Save Morning Routine
            </GlassButton>
          </>
        )}
      </div>
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import { Card, Button } from "./ui";
import { theme } from "../styles/theme";
import { showError, showSuccess } from "../lib/toast";
import { TrendingUp, TrendingDown, BarChart3, Target, DollarSign, Calendar } from "lucide-react";
import { useIsMobile } from "../hooks/useBreakpoint";

interface PatternPerformance {
  pattern_type: string;
  total_occurrences: number;
  successful_trades: number;
  failed_trades: number;
  win_rate: number;
  avg_roi: number;
  avg_hold_days: number;
  best_roi: number;
  worst_roi: number;
  last_seen: string;
}

interface BacktestResult {
  symbol: string;
  start_date: string;
  end_date: string;
  patterns: PatternPerformance[];
  total_patterns: number;
  overall_win_rate: number;
  overall_avg_roi: number;
}

export default function PatternBacktestDashboard() {
  const isMobile = useIsMobile();
  const [symbol, setSymbol] = useState("SPY");
  const [lookbackDays, setLookbackDays] = useState(365);
  const [minConfidence, setMinConfidence] = useState(0.7);
  const [isLoading, setIsLoading] = useState(false);
  const [result, setResult] = useState<BacktestResult | null>(null);

  const runBacktest = async () => {
    if (!symbol.trim()) {
      showError("Please enter a symbol");
      return;
    }

    setIsLoading(true);
    setResult(null);

    try {
      const res = await fetch(
        `/api/proxy/api/ml/backtest-patterns?symbol=${symbol.toUpperCase()}&lookback_days=${lookbackDays}&min_confidence=${minConfidence}`,
        { method: "POST" }
      );

      if (!res.ok) {
        throw new Error(`Backtest failed: ${res.statusText}`);
      }

      const data = await res.json();
      setResult(data);
      showSuccess(`Backtest complete! Found ${data.total_patterns} patterns.`);
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : "Unknown error";
      showError(`Backtest failed: ${errorMessage}`);
    } finally {
      setIsLoading(false);
    }
  };

  const getWinRateColor = (winRate: number): string => {
    if (winRate >= 70) return theme.colors.success;
    if (winRate >= 50) return theme.colors.warning;
    return theme.colors.error;
  };

  const getROIColor = (roi: number): string => {
    if (roi >= 5) return theme.colors.success;
    if (roi >= 0) return theme.colors.warning;
    return theme.colors.error;
  };

  const formatPatternName = (pattern: string): string => {
    return pattern
      .split("_")
      .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
      .join(" ");
  };

  return (
    <div style={{ padding: isMobile ? theme.spacing.md : theme.spacing.lg }}>
      {/* Header */}
      <div style={{ marginBottom: theme.spacing.lg }}>
        <h2
          style={{
            margin: 0,
            fontSize: isMobile ? "24px" : "32px",
            fontWeight: "700",
            color: theme.colors.text,
            textShadow: theme.glow.green,
            marginBottom: theme.spacing.xs,
            display: "flex",
            alignItems: "center",
            gap: theme.spacing.sm,
          }}
        >
          <BarChart3 size={32} color={theme.colors.primary} />
          Pattern Backtesting Dashboard
        </h2>
        <p
          style={{
            margin: 0,
            fontSize: "14px",
            color: theme.colors.textMuted,
          }}
        >
          Analyze historical performance of detected chart patterns
        </p>
      </div>

      {/* Configuration Card */}
      <Card glow="green" style={{ marginBottom: theme.spacing.lg }}>
        <h3
          style={{
            fontSize: isMobile ? "18px" : "20px",
            fontWeight: "600",
            color: theme.colors.text,
            marginBottom: theme.spacing.lg,
          }}
        >
          Backtest Configuration
        </h3>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: isMobile ? "1fr" : "repeat(3, 1fr)",
            gap: theme.spacing.md,
            marginBottom: theme.spacing.lg,
          }}
        >
          {/* Symbol Input */}
          <div>
            <label
              style={{
                display: "block",
                fontSize: "12px",
                color: theme.colors.textMuted,
                marginBottom: theme.spacing.xs,
                textTransform: "uppercase",
                letterSpacing: "0.5px",
              }}
            >
              Symbol
            </label>
            <input
              type="text"
              value={symbol}
              onChange={(e) => setSymbol(e.target.value.toUpperCase())}
              disabled={isLoading}
              placeholder="SPY"
              style={{
                width: "100%",
                padding: "12px",
                background: "rgba(15, 23, 42, 0.5)",
                border: `1px solid ${theme.colors.border}`,
                borderRadius: theme.borderRadius.md,
                color: theme.colors.text,
                fontSize: "14px",
              }}
            />
          </div>

          {/* Lookback Days */}
          <div>
            <label
              style={{
                display: "block",
                fontSize: "12px",
                color: theme.colors.textMuted,
                marginBottom: theme.spacing.xs,
                textTransform: "uppercase",
                letterSpacing: "0.5px",
              }}
            >
              Lookback Days: {lookbackDays}
            </label>
            <input
              type="range"
              min="30"
              max="730"
              step="30"
              value={lookbackDays}
              onChange={(e) => setLookbackDays(Number(e.target.value))}
              disabled={isLoading}
              style={{
                width: "100%",
                height: "8px",
                borderRadius: "4px",
                outline: "none",
                opacity: isLoading ? 0.5 : 1,
              }}
            />
            <div
              style={{
                fontSize: "11px",
                color: theme.colors.textMuted,
                marginTop: theme.spacing.xs,
              }}
            >
              30 days - 2 years
            </div>
          </div>

          {/* Min Confidence */}
          <div>
            <label
              style={{
                display: "block",
                fontSize: "12px",
                color: theme.colors.textMuted,
                marginBottom: theme.spacing.xs,
                textTransform: "uppercase",
                letterSpacing: "0.5px",
              }}
            >
              Min Confidence: {(minConfidence * 100).toFixed(0)}%
            </label>
            <input
              type="range"
              min="0.5"
              max="0.95"
              step="0.05"
              value={minConfidence}
              onChange={(e) => setMinConfidence(Number(e.target.value))}
              disabled={isLoading}
              style={{
                width: "100%",
                height: "8px",
                borderRadius: "4px",
                outline: "none",
                opacity: isLoading ? 0.5 : 1,
              }}
            />
            <div
              style={{
                fontSize: "11px",
                color: theme.colors.textMuted,
                marginTop: theme.spacing.xs,
              }}
            >
              50% - 95%
            </div>
          </div>
        </div>

        <Button
          onClick={runBacktest}
          loading={isLoading}
          disabled={isLoading || !symbol.trim()}
          variant="primary"
          style={{ width: isMobile ? "100%" : "auto" }}
        >
          {isLoading ? "Running Backtest..." : "Run Pattern Backtest"}
        </Button>
      </Card>

      {/* Results */}
      {result && (
        <>
          {/* Summary Card */}
          <Card glow="cyan" style={{ marginBottom: theme.spacing.lg }}>
            <h3
              style={{
                fontSize: isMobile ? "18px" : "20px",
                fontWeight: "600",
                color: theme.colors.text,
                marginBottom: theme.spacing.lg,
              }}
            >
              üìä Backtest Summary - {result.symbol}
            </h3>

            <div
              style={{
                display: "grid",
                gridTemplateColumns: isMobile ? "1fr" : "repeat(4, 1fr)",
                gap: theme.spacing.md,
              }}
            >
              {/* Total Patterns */}
              <div
                style={{
                  padding: theme.spacing.md,
                  background: "rgba(6, 182, 212, 0.1)",
                  border: `1px solid ${theme.colors.secondary}`,
                  borderRadius: theme.borderRadius.md,
                }}
              >
                <div
                  style={{
                    fontSize: "12px",
                    color: theme.colors.textMuted,
                    marginBottom: theme.spacing.xs,
                  }}
                >
                  TOTAL PATTERNS
                </div>
                <div
                  style={{
                    fontSize: "32px",
                    fontWeight: "700",
                    color: theme.colors.secondary,
                  }}
                >
                  {result.total_patterns}
                </div>
              </div>

              {/* Overall Win Rate */}
              <div
                style={{
                  padding: theme.spacing.md,
                  background: `rgba(${result.overall_win_rate >= 50 ? "16, 185, 129" : "239, 68, 68"}, 0.1)`,
                  border: `1px solid ${getWinRateColor(result.overall_win_rate)}`,
                  borderRadius: theme.borderRadius.md,
                }}
              >
                <div
                  style={{
                    fontSize: "12px",
                    color: theme.colors.textMuted,
                    marginBottom: theme.spacing.xs,
                  }}
                >
                  WIN RATE
                </div>
                <div
                  style={{
                    fontSize: "32px",
                    fontWeight: "700",
                    color: getWinRateColor(result.overall_win_rate),
                  }}
                >
                  {result.overall_win_rate.toFixed(1)}%
                </div>
              </div>

              {/* Overall Avg ROI */}
              <div
                style={{
                  padding: theme.spacing.md,
                  background: `rgba(${result.overall_avg_roi >= 0 ? "16, 185, 129" : "239, 68, 68"}, 0.1)`,
                  border: `1px solid ${getROIColor(result.overall_avg_roi)}`,
                  borderRadius: theme.borderRadius.md,
                }}
              >
                <div
                  style={{
                    fontSize: "12px",
                    color: theme.colors.textMuted,
                    marginBottom: theme.spacing.xs,
                  }}
                >
                  AVG ROI
                </div>
                <div
                  style={{
                    fontSize: "32px",
                    fontWeight: "700",
                    color: getROIColor(result.overall_avg_roi),
                  }}
                >
                  {result.overall_avg_roi >= 0 ? "+" : ""}
                  {result.overall_avg_roi.toFixed(2)}%
                </div>
              </div>

              {/* Date Range */}
              <div
                style={{
                  padding: theme.spacing.md,
                  background: "rgba(139, 92, 246, 0.1)",
                  border: `1px solid ${theme.colors.accent}`,
                  borderRadius: theme.borderRadius.md,
                }}
              >
                <div
                  style={{
                    fontSize: "12px",
                    color: theme.colors.textMuted,
                    marginBottom: theme.spacing.xs,
                  }}
                >
                  DATE RANGE
                </div>
                <div
                  style={{
                    fontSize: "11px",
                    fontWeight: "600",
                    color: theme.colors.accent,
                  }}
                >
                  {new Date(result.start_date).toLocaleDateString()}
                  <br />‚Üí{" "}
                  {new Date(result.end_date).toLocaleDateString()}
                </div>
              </div>
            </div>
          </Card>

          {/* Pattern Performance Table */}
          <Card glow="purple" style={{ marginBottom: theme.spacing.lg }}>
            <h3
              style={{
                fontSize: isMobile ? "18px" : "20px",
                fontWeight: "600",
                color: theme.colors.text,
                marginBottom: theme.spacing.lg,
              }}
            >
              üìà Pattern Performance Breakdown
            </h3>

            {result.patterns.length === 0 ? (
              <div
                style={{
                  textAlign: "center",
                  padding: theme.spacing.xl,
                  color: theme.colors.textMuted,
                }}
              >
                No patterns detected during the backtest period.
                <br />
                Try adjusting the lookback days or minimum confidence.
              </div>
            ) : (
              <div style={{ overflowX: "auto" }}>
                <table
                  style={{
                    width: "100%",
                    borderCollapse: "collapse",
                    fontSize: isMobile ? "12px" : "14px",
                  }}
                >
                  <thead>
                    <tr
                      style={{
                        borderBottom: `1px solid ${theme.colors.border}`,
                      }}
                    >
                      <th
                        style={{
                          padding: theme.spacing.sm,
                          textAlign: "left",
                          color: theme.colors.textMuted,
                          fontWeight: "600",
                          textTransform: "uppercase",
                          fontSize: "11px",
                        }}
                      >
                        Pattern
                      </th>
                      <th
                        style={{
                          padding: theme.spacing.sm,
                          textAlign: "center",
                          color: theme.colors.textMuted,
                          fontWeight: "600",
                          textTransform: "uppercase",
                          fontSize: "11px",
                        }}
                      >
                        Count
                      </th>
                      <th
                        style={{
                          padding: theme.spacing.sm,
                          textAlign: "center",
                          color: theme.colors.textMuted,
                          fontWeight: "600",
                          textTransform: "uppercase",
                          fontSize: "11px",
                        }}
                      >
                        Win/Loss
                      </th>
                      <th
                        style={{
                          padding: theme.spacing.sm,
                          textAlign: "center",
                          color: theme.colors.textMuted,
                          fontWeight: "600",
                          textTransform: "uppercase",
                          fontSize: "11px",
                        }}
                      >
                        Win Rate
                      </th>
                      <th
                        style={{
                          padding: theme.spacing.sm,
                          textAlign: "center",
                          color: theme.colors.textMuted,
                          fontWeight: "600",
                          textTransform: "uppercase",
                          fontSize: "11px",
                        }}
                      >
                        Avg ROI
                      </th>
                      <th
                        style={{
                          padding: theme.spacing.sm,
                          textAlign: "center",
                          color: theme.colors.textMuted,
                          fontWeight: "600",
                          textTransform: "uppercase",
                          fontSize: "11px",
                        }}
                      >
                        Best/Worst
                      </th>
                      <th
                        style={{
                          padding: theme.spacing.sm,
                          textAlign: "center",
                          color: theme.colors.textMuted,
                          fontWeight: "600",
                          textTransform: "uppercase",
                          fontSize: "11px",
                        }}
                      >
                        Avg Hold
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    {result.patterns
                      .sort((a, b) => b.win_rate - a.win_rate)
                      .map((pattern, idx) => (
                        <tr
                          key={idx}
                          style={{
                            borderBottom: `1px solid ${theme.colors.border}`,
                          }}
                        >
                          {/* Pattern Name */}
                          <td
                            style={{
                              padding: theme.spacing.sm,
                              color: theme.colors.text,
                              fontWeight: "600",
                            }}
                          >
                            {formatPatternName(pattern.pattern_type)}
                          </td>

                          {/* Count */}
                          <td
                            style={{
                              padding: theme.spacing.sm,
                              textAlign: "center",
                              color: theme.colors.secondary,
                              fontWeight: "600",
                            }}
                          >
                            {pattern.total_occurrences}
                          </td>

                          {/* Win/Loss */}
                          <td
                            style={{
                              padding: theme.spacing.sm,
                              textAlign: "center",
                            }}
                          >
                            <span style={{ color: theme.colors.success }}>
                              {pattern.successful_trades}
                            </span>
                            {" / "}
                            <span style={{ color: theme.colors.error }}>
                              {pattern.failed_trades}
                            </span>
                          </td>

                          {/* Win Rate */}
                          <td
                            style={{
                              padding: theme.spacing.sm,
                              textAlign: "center",
                            }}
                          >
                            <div
                              style={{
                                display: "inline-block",
                                padding: "4px 12px",
                                background: `rgba(${pattern.win_rate >= 50 ? "16, 185, 129" : "239, 68, 68"}, 0.2)`,
                                border: `1px solid ${getWinRateColor(pattern.win_rate)}`,
                                borderRadius: theme.borderRadius.sm,
                                color: getWinRateColor(pattern.win_rate),
                                fontWeight: "700",
                              }}
                            >
                              {pattern.win_rate.toFixed(1)}%
                            </div>
                          </td>

                          {/* Avg ROI */}
                          <td
                            style={{
                              padding: theme.spacing.sm,
                              textAlign: "center",
                              color: getROIColor(pattern.avg_roi),
                              fontWeight: "700",
                            }}
                          >
                            {pattern.avg_roi >= 0 ? "+" : ""}
                            {pattern.avg_roi.toFixed(2)}%
                          </td>

                          {/* Best/Worst */}
                          <td
                            style={{
                              padding: theme.spacing.sm,
                              textAlign: "center",
                              fontSize: "12px",
                            }}
                          >
                            <div style={{ color: theme.colors.success }}>
                              +{pattern.best_roi.toFixed(1)}%
                            </div>
                            <div style={{ color: theme.colors.error }}>
                              {pattern.worst_roi.toFixed(1)}%
                            </div>
                          </td>

                          {/* Avg Hold */}
                          <td
                            style={{
                              padding: theme.spacing.sm,
                              textAlign: "center",
                              color: theme.colors.textMuted,
                            }}
                          >
                            {pattern.avg_hold_days.toFixed(1)}d
                          </td>
                        </tr>
                      ))}
                  </tbody>
                </table>
              </div>
            )}
          </Card>
        </>
      )}
    </div>
  );
}

"use client";

import { useState } from "react";
import { Card, Button } from "./ui";
import { theme } from "../styles/theme";
import { showError, showSuccess } from "../lib/toast";
import { Target, TrendingUp, Shield, PieChart, AlertTriangle, Sparkles, DollarSign } from "lucide-react";
import { useIsMobile } from "../hooks/useBreakpoint";

interface Position {
  symbol: string;
  shares: number;
  current_price: number;
  market_value: number;
  weight: number;
}

interface OptimizationSuggestion {
  symbol: string;
  action: "buy" | "sell" | "hold";
  current_shares: number;
  suggested_shares: number;
  shares_delta: number;
  current_weight: number;
  target_weight: number;
  reasoning: string;
  expected_return: number;
  risk_score: number;
}

interface PortfolioMetrics {
  total_value: number;
  expected_return: number;
  volatility: number;
  sharpe_ratio: number;
  diversification_score: number;
  risk_level: "low" | "moderate" | "high";
}

interface OptimizationResult {
  current_portfolio: PortfolioMetrics;
  optimized_portfolio: PortfolioMetrics;
  suggestions: OptimizationSuggestion[];
  risk_adjusted: boolean;
  optimization_method: string;
  estimated_improvement: number;
}

export default function PortfolioOptimizer() {
  const isMobile = useIsMobile();
  const [riskTolerance, setRiskTolerance] = useState<"conservative" | "moderate" | "aggressive">("moderate");
  const [targetReturn, setTargetReturn] = useState(12);
  const [isOptimizing, setIsOptimizing] = useState(false);
  const [result, setResult] = useState<OptimizationResult | null>(null);

  const optimizePortfolio = async () => {
    setIsOptimizing(true);
    try {
      const res = await fetch(
        `/api/proxy/api/ml/optimize-portfolio?risk_tolerance=${riskTolerance}&target_return=${targetReturn}`,
        { method: "POST" }
      );

      if (!res.ok) throw new Error("Optimization failed");
      const data = await res.json();
      setResult(data);
      showSuccess("Portfolio optimization complete!");
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : "Unknown error";
      showError(`Optimization failed: ${errorMessage}`);
    } finally {
      setIsOptimizing(false);
    }
  };

  const getRiskColor = (riskLevel: string): string => {
    switch (riskLevel) {
      case "low":
        return theme.colors.success;
      case "moderate":
        return theme.colors.warning;
      case "high":
        return theme.colors.error;
      default:
        return theme.colors.text;
    }
  };

  const getActionColor = (action: string): string => {
    switch (action) {
      case "buy":
        return theme.colors.success;
      case "sell":
        return theme.colors.error;
      case "hold":
        return theme.colors.textMuted;
      default:
        return theme.colors.text;
    }
  };

  const getActionIcon = (action: string) => {
    switch (action) {
      case "buy":
        return <TrendingUp size={18} color={theme.colors.success} />;
      case "sell":
        return <AlertTriangle size={18} color={theme.colors.error} />;
      case "hold":
        return <Shield size={18} color={theme.colors.textMuted} />;
      default:
        return null;
    }
  };

  return (
    <div style={{ padding: isMobile ? theme.spacing.md : theme.spacing.lg }}>
      {/* Header */}
      <div style={{ marginBottom: theme.spacing.lg }}>
        <h2
          style={{
            margin: 0,
            fontSize: isMobile ? "24px" : "32px",
            fontWeight: "700",
            color: theme.colors.text,
            textShadow: theme.glow.cyan,
            marginBottom: theme.spacing.xs,
            display: "flex",
            alignItems: "center",
            gap: theme.spacing.sm,
          }}
        >
          <Target size={32} color={theme.colors.secondary} />
          AI Portfolio Optimizer
        </h2>
        <p
          style={{
            margin: 0,
            fontSize: "14px",
            color: theme.colors.textMuted,
          }}
        >
          ML-powered portfolio rebalancing and risk optimization
        </p>
      </div>

      {/* Configuration Card */}
      <Card glow="cyan" style={{ marginBottom: theme.spacing.lg }}>
        <h3
          style={{
            fontSize: isMobile ? "18px" : "20px",
            fontWeight: "600",
            color: theme.colors.text,
            marginBottom: theme.spacing.lg,
          }}
        >
          Optimization Parameters
        </h3>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: isMobile ? "1fr" : "repeat(2, 1fr)",
            gap: theme.spacing.lg,
            marginBottom: theme.spacing.lg,
          }}
        >
          {/* Risk Tolerance */}
          <div>
            <label
              style={{
                display: "block",
                fontSize: "12px",
                color: theme.colors.textMuted,
                marginBottom: theme.spacing.sm,
                textTransform: "uppercase",
                letterSpacing: "0.5px",
              }}
            >
              Risk Tolerance
            </label>
            <div style={{ display: "flex", gap: theme.spacing.xs }}>
              {["conservative", "moderate", "aggressive"].map((level) => (
                <button
                  key={level}
                  onClick={() => setRiskTolerance(level as typeof riskTolerance)}
                  disabled={isOptimizing}
                  style={{
                    flex: 1,
                    padding: "12px",
                    background:
                      riskTolerance === level
                        ? `linear-gradient(135deg, ${theme.colors.secondary}, ${theme.colors.primary})`
                        : "rgba(15, 23, 42, 0.5)",
                    border: `1px solid ${riskTolerance === level ? theme.colors.secondary : theme.colors.border}`,
                    borderRadius: theme.borderRadius.md,
                    color: theme.colors.text,
                    fontSize: "13px",
                    fontWeight: riskTolerance === level ? "700" : "400",
                    cursor: isOptimizing ? "not-allowed" : "pointer",
                    textTransform: "capitalize",
                    transition: "all 0.2s",
                  }}
                >
                  {level}
                </button>
              ))}
            </div>
          </div>

          {/* Target Return */}
          <div>
            <label
              style={{
                display: "block",
                fontSize: "12px",
                color: theme.colors.textMuted,
                marginBottom: theme.spacing.sm,
                textTransform: "uppercase",
                letterSpacing: "0.5px",
              }}
            >
              Target Annual Return: {targetReturn}%
            </label>
            <input
              type="range"
              min="5"
              max="30"
              step="1"
              value={targetReturn}
              onChange={(e) => setTargetReturn(Number(e.target.value))}
              disabled={isOptimizing}
              style={{
                width: "100%",
                height: "8px",
                borderRadius: "4px",
                outline: "none",
                opacity: isOptimizing ? 0.5 : 1,
              }}
            />
            <div
              style={{
                fontSize: "11px",
                color: theme.colors.textMuted,
                marginTop: theme.spacing.xs,
              }}
            >
              5% (conservative) - 30% (aggressive)
            </div>
          </div>
        </div>

        <Button
          onClick={optimizePortfolio}
          loading={isOptimizing}
          disabled={isOptimizing}
          variant="primary"
          style={{ width: isMobile ? "100%" : "auto" }}
        >
          {isOptimizing ? "Optimizing..." : "Optimize Portfolio"}
        </Button>
      </Card>

      {/* Results */}
      {result && (
        <>
          {/* Comparison Card */}
          <Card glow="purple" style={{ marginBottom: theme.spacing.lg }}>
            <h3
              style={{
                fontSize: isMobile ? "18px" : "20px",
                fontWeight: "600",
                color: theme.colors.text,
                marginBottom: theme.spacing.lg,
                display: "flex",
                alignItems: "center",
                gap: theme.spacing.sm,
              }}
            >
              <Sparkles size={24} color={theme.colors.accent} />
              Portfolio Comparison
            </h3>

            <div
              style={{
                display: "grid",
                gridTemplateColumns: isMobile ? "1fr" : "repeat(2, 1fr)",
                gap: theme.spacing.lg,
              }}
            >
              {/* Current Portfolio */}
              <div
                style={{
                  padding: theme.spacing.md,
                  background: "rgba(15, 23, 42, 0.5)",
                  border: `1px solid ${theme.colors.border}`,
                  borderRadius: theme.borderRadius.md,
                }}
              >
                <h4
                  style={{
                    margin: 0,
                    fontSize: "16px",
                    fontWeight: "600",
                    color: theme.colors.textMuted,
                    marginBottom: theme.spacing.md,
                  }}
                >
                  Current Portfolio
                </h4>

                <div style={{ display: "grid", gap: theme.spacing.sm }}>
                  <div style={{ display: "flex", justifyContent: "space-between" }}>
                    <span style={{ fontSize: "13px", color: theme.colors.textMuted }}>Total Value</span>
                    <span style={{ fontSize: "16px", fontWeight: "700", color: theme.colors.text }}>
                      ${result.current_portfolio.total_value.toLocaleString()}
                    </span>
                  </div>
                  <div style={{ display: "flex", justifyContent: "space-between" }}>
                    <span style={{ fontSize: "13px", color: theme.colors.textMuted }}>Expected Return</span>
                    <span style={{ fontSize: "16px", fontWeight: "700", color: theme.colors.text }}>
                      {result.current_portfolio.expected_return.toFixed(2)}%
                    </span>
                  </div>
                  <div style={{ display: "flex", justifyContent: "space-between" }}>
                    <span style={{ fontSize: "13px", color: theme.colors.textMuted }}>Volatility</span>
                    <span style={{ fontSize: "16px", fontWeight: "700", color: theme.colors.warning }}>
                      {result.current_portfolio.volatility.toFixed(2)}%
                    </span>
                  </div>
                  <div style={{ display: "flex", justifyContent: "space-between" }}>
                    <span style={{ fontSize: "13px", color: theme.colors.textMuted }}>Sharpe Ratio</span>
                    <span style={{ fontSize: "16px", fontWeight: "700", color: theme.colors.secondary }}>
                      {result.current_portfolio.sharpe_ratio.toFixed(2)}
                    </span>
                  </div>
                  <div style={{ display: "flex", justifyContent: "space-between" }}>
                    <span style={{ fontSize: "13px", color: theme.colors.textMuted }}>Diversification</span>
                    <span
                      style={{
                        fontSize: "16px",
                        fontWeight: "700",
                        color: getRiskColor(result.current_portfolio.risk_level),
                      }}
                    >
                      {result.current_portfolio.diversification_score.toFixed(0)}%
                    </span>
                  </div>
                </div>
              </div>

              {/* Optimized Portfolio */}
              <div
                style={{
                  padding: theme.spacing.md,
                  background: `linear-gradient(135deg, rgba(16, 185, 129, 0.1), rgba(6, 182, 212, 0.1))`,
                  border: `2px solid ${theme.colors.success}`,
                  borderRadius: theme.borderRadius.md,
                  boxShadow: theme.glow.green,
                }}
              >
                <h4
                  style={{
                    margin: 0,
                    fontSize: "16px",
                    fontWeight: "600",
                    color: theme.colors.success,
                    marginBottom: theme.spacing.md,
                  }}
                >
                  ‚ú® Optimized Portfolio
                </h4>

                <div style={{ display: "grid", gap: theme.spacing.sm }}>
                  <div style={{ display: "flex", justifyContent: "space-between" }}>
                    <span style={{ fontSize: "13px", color: theme.colors.textMuted }}>Total Value</span>
                    <span style={{ fontSize: "16px", fontWeight: "700", color: theme.colors.text }}>
                      ${result.optimized_portfolio.total_value.toLocaleString()}
                    </span>
                  </div>
                  <div style={{ display: "flex", justifyContent: "space-between" }}>
                    <span style={{ fontSize: "13px", color: theme.colors.textMuted }}>Expected Return</span>
                    <span style={{ fontSize: "16px", fontWeight: "700", color: theme.colors.success }}>
                      {result.optimized_portfolio.expected_return.toFixed(2)}%{" "}
                      <span style={{ fontSize: "12px" }}>
                        (+{(result.optimized_portfolio.expected_return - result.current_portfolio.expected_return).toFixed(2)}%)
                      </span>
                    </span>
                  </div>
                  <div style={{ display: "flex", justifyContent: "space-between" }}>
                    <span style={{ fontSize: "13px", color: theme.colors.textMuted }}>Volatility</span>
                    <span style={{ fontSize: "16px", fontWeight: "700", color: theme.colors.success }}>
                      {result.optimized_portfolio.volatility.toFixed(2)}%{" "}
                      <span style={{ fontSize: "12px" }}>
                        ({result.optimized_portfolio.volatility < result.current_portfolio.volatility ? "-" : "+"}
                        {Math.abs(result.optimized_portfolio.volatility - result.current_portfolio.volatility).toFixed(2)}%)
                      </span>
                    </span>
                  </div>
                  <div style={{ display: "flex", justifyContent: "space-between" }}>
                    <span style={{ fontSize: "13px", color: theme.colors.textMuted }}>Sharpe Ratio</span>
                    <span style={{ fontSize: "16px", fontWeight: "700", color: theme.colors.success }}>
                      {result.optimized_portfolio.sharpe_ratio.toFixed(2)}{" "}
                      <span style={{ fontSize: "12px" }}>
                        (+{(result.optimized_portfolio.sharpe_ratio - result.current_portfolio.sharpe_ratio).toFixed(2)})
                      </span>
                    </span>
                  </div>
                  <div style={{ display: "flex", justifyContent: "space-between" }}>
                    <span style={{ fontSize: "13px", color: theme.colors.textMuted }}>Diversification</span>
                    <span style={{ fontSize: "16px", fontWeight: "700", color: theme.colors.success }}>
                      {result.optimized_portfolio.diversification_score.toFixed(0)}%{" "}
                      <span style={{ fontSize: "12px" }}>
                        (+{(result.optimized_portfolio.diversification_score - result.current_portfolio.diversification_score).toFixed(0)}%)
                      </span>
                    </span>
                  </div>
                </div>
              </div>
            </div>

            {/* Improvement Badge */}
            <div
              style={{
                marginTop: theme.spacing.lg,
                padding: theme.spacing.md,
                background: "rgba(16, 185, 129, 0.1)",
                border: `1px solid ${theme.colors.success}`,
                borderRadius: theme.borderRadius.md,
                textAlign: "center",
              }}
            >
              <div style={{ fontSize: "13px", color: theme.colors.textMuted, marginBottom: theme.spacing.xs }}>
                Estimated Improvement
              </div>
              <div
                style={{
                  fontSize: "32px",
                  fontWeight: "700",
                  color: theme.colors.success,
                }}
              >
                +{result.estimated_improvement.toFixed(1)}%
              </div>
              <div style={{ fontSize: "12px", color: theme.colors.textMuted, marginTop: theme.spacing.xs }}>
                Risk-adjusted annual return improvement
              </div>
            </div>
          </Card>

          {/* Suggestions */}
          <Card glow="green" style={{ marginBottom: theme.spacing.lg }}>
            <h3
              style={{
                fontSize: isMobile ? "18px" : "20px",
                fontWeight: "600",
                color: theme.colors.text,
                marginBottom: theme.spacing.lg,
                display: "flex",
                alignItems: "center",
                gap: theme.spacing.sm,
              }}
            >
              <PieChart size={24} color={theme.colors.primary} />
              Rebalancing Suggestions
            </h3>

            <div style={{ display: "grid", gap: theme.spacing.md }}>
              {result.suggestions.map((suggestion, idx) => (
                <div
                  key={idx}
                  style={{
                    padding: theme.spacing.md,
                    background: "rgba(15, 23, 42, 0.5)",
                    border: `1px solid ${getActionColor(suggestion.action)}`,
                    borderRadius: theme.borderRadius.md,
                  }}
                >
                  <div
                    style={{
                      display: "flex",
                      justifyContent: "space-between",
                      alignItems: "flex-start",
                      marginBottom: theme.spacing.sm,
                    }}
                  >
                    <div style={{ display: "flex", alignItems: "center", gap: theme.spacing.sm }}>
                      <div
                        style={{
                          fontSize: "20px",
                          fontWeight: "700",
                          color: theme.colors.text,
                        }}
                      >
                        {suggestion.symbol}
                      </div>
                      <div
                        style={{
                          display: "flex",
                          alignItems: "center",
                          gap: "4px",
                          padding: "4px 12px",
                          background: `rgba(${
                            suggestion.action === "buy"
                              ? "16, 185, 129"
                              : suggestion.action === "sell"
                                ? "239, 68, 68"
                                : "148, 163, 184"
                          }, 0.2)`,
                          border: `1px solid ${getActionColor(suggestion.action)}`,
                          borderRadius: theme.borderRadius.sm,
                        }}
                      >
                        {getActionIcon(suggestion.action)}
                        <span
                          style={{
                            fontSize: "12px",
                            fontWeight: "700",
                            color: getActionColor(suggestion.action),
                            textTransform: "uppercase",
                          }}
                        >
                          {suggestion.action}
                        </span>
                      </div>
                    </div>

                    <div style={{ textAlign: "right" }}>
                      <div style={{ fontSize: "12px", color: theme.colors.textMuted }}>
                        {suggestion.current_shares} ‚Üí {suggestion.suggested_shares} shares
                      </div>
                      <div
                        style={{
                          fontSize: "16px",
                          fontWeight: "700",
                          color: suggestion.shares_delta >= 0 ? theme.colors.success : theme.colors.error,
                        }}
                      >
                        {suggestion.shares_delta >= 0 ? "+" : ""}
                        {suggestion.shares_delta}
                      </div>
                    </div>
                  </div>

                  <div
                    style={{
                      fontSize: "13px",
                      color: theme.colors.text,
                      marginBottom: theme.spacing.sm,
                      lineHeight: "1.6",
                    }}
                  >
                    {suggestion.reasoning}
                  </div>

                  <div
                    style={{
                      display: "grid",
                      gridTemplateColumns: isMobile ? "1fr" : "repeat(4, 1fr)",
                      gap: theme.spacing.sm,
                      fontSize: "12px",
                    }}
                  >
                    <div>
                      <span style={{ color: theme.colors.textMuted }}>Current Weight: </span>
                      <span style={{ color: theme.colors.text, fontWeight: "600" }}>
                        {suggestion.current_weight.toFixed(1)}%
                      </span>
                    </div>
                    <div>
                      <span style={{ color: theme.colors.textMuted }}>Target Weight: </span>
                      <span style={{ color: theme.colors.secondary, fontWeight: "600" }}>
                        {suggestion.target_weight.toFixed(1)}%
                      </span>
                    </div>
                    <div>
                      <span style={{ color: theme.colors.textMuted }}>Expected Return: </span>
                      <span style={{ color: theme.colors.success, fontWeight: "600" }}>
                        {suggestion.expected_return.toFixed(1)}%
                      </span>
                    </div>
                    <div>
                      <span style={{ color: theme.colors.textMuted }}>Risk Score: </span>
                      <span style={{ color: getRiskColor(suggestion.risk_score > 0.7 ? "high" : suggestion.risk_score > 0.4 ? "moderate" : "low"), fontWeight: "600" }}>
                        {(suggestion.risk_score * 100).toFixed(0)}
                      </span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </Card>
        </>
      )}
    </div>
  );
}

"use client";

import { useState, useEffect } from "react";
import { Card, Button } from "./ui";
import { theme } from "../styles/theme";
import { showError, showSuccess } from "../lib/toast";
import { Newspaper, TrendingUp, TrendingDown, Minus, RefreshCw, Sparkles } from "lucide-react";
import { useIsMobile } from "../hooks/useBreakpoint";

interface NewsSentiment {
  article_id: string;
  title: string;
  source: string;
  published_at: string;
  url: string;
  sentiment: "bullish" | "bearish" | "neutral";
  sentiment_score: number;
  confidence: number;
  key_topics: string[];
  impact_score: number;
}

interface SentimentAnalysis {
  symbol: string;
  overall_sentiment: "bullish" | "bearish" | "neutral";
  sentiment_score: number;
  confidence: number;
  bullish_count: number;
  bearish_count: number;
  neutral_count: number;
  total_articles: number;
  avg_impact: number;
  top_topics: string[];
  articles: NewsSentiment[];
  timestamp: string;
}

export default function SentimentDashboard() {
  const isMobile = useIsMobile();
  const [symbol, setSymbol] = useState("SPY");
  const [lookbackHours, setLookbackHours] = useState(24);
  const [isLoading, setIsLoading] = useState(false);
  const [result, setResult] = useState<SentimentAnalysis | null>(null);
  const [autoRefresh, setAutoRefresh] = useState(false);

  // Auto-refresh every 5 minutes if enabled
  useEffect(() => {
    if (!autoRefresh || !result) return;

    const interval = setInterval(() => {
      analyzeSentiment();
    }, 5 * 60 * 1000); // 5 minutes

    return () => clearInterval(interval);
  }, [autoRefresh, result]);

  const analyzeSentiment = async () => {
    if (!symbol.trim()) {
      showError("Please enter a symbol");
      return;
    }

    setIsLoading(true);

    try {
      const res = await fetch(
        `/api/proxy/api/sentiment/analyze?symbol=${symbol.toUpperCase()}&lookback_hours=${lookbackHours}`
      );

      if (!res.ok) {
        throw new Error(`Sentiment analysis failed: ${res.statusText}`);
      }

      const data = await res.json();
      setResult(data);
      showSuccess(`Analyzed ${data.total_articles} articles for ${data.symbol}`);
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : "Unknown error";
      showError(`Analysis failed: ${errorMessage}`);
    } finally {
      setIsLoading(false);
    }
  };

  const getSentimentColor = (sentiment: string): string => {
    switch (sentiment) {
      case "bullish":
        return theme.colors.success;
      case "bearish":
        return theme.colors.error;
      case "neutral":
        return theme.colors.textMuted;
      default:
        return theme.colors.text;
    }
  };

  const getSentimentIcon = (sentiment: string) => {
    switch (sentiment) {
      case "bullish":
        return <TrendingUp size={24} color={theme.colors.success} />;
      case "bearish":
        return <TrendingDown size={24} color={theme.colors.error} />;
      case "neutral":
        return <Minus size={24} color={theme.colors.textMuted} />;
      default:
        return <Minus size={24} />;
    }
  };

  const getSentimentEmoji = (sentiment: string): string => {
    switch (sentiment) {
      case "bullish":
        return "üöÄ";
      case "bearish":
        return "üìâ";
      case "neutral":
        return "‚û°Ô∏è";
      default:
        return "‚ùì";
    }
  };

  const formatTimestamp = (timestamp: string): string => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffMs = now.getTime() - date.getTime();
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
    const diffMins = Math.floor(diffMs / (1000 * 60));

    if (diffMins < 60) return `${diffMins}m ago`;
    if (diffHours < 24) return `${diffHours}h ago`;
    return date.toLocaleDateString();
  };

  return (
    <div style={{ padding: isMobile ? theme.spacing.md : theme.spacing.lg }}>
      {/* Header */}
      <div style={{ marginBottom: theme.spacing.lg }}>
        <h2
          style={{
            margin: 0,
            fontSize: isMobile ? "24px" : "32px",
            fontWeight: "700",
            color: theme.colors.text,
            textShadow: theme.glow.cyan,
            marginBottom: theme.spacing.xs,
            display: "flex",
            alignItems: "center",
            gap: theme.spacing.sm,
          }}
        >
          <Newspaper size={32} color={theme.colors.secondary} />
          News Sentiment Dashboard
        </h2>
        <p
          style={{
            margin: 0,
            fontSize: "14px",
            color: theme.colors.textMuted,
          }}
        >
          Real-time AI-powered news sentiment analysis
        </p>
      </div>

      {/* Configuration Card */}
      <Card glow="cyan" style={{ marginBottom: theme.spacing.lg }}>
        <h3
          style={{
            fontSize: isMobile ? "18px" : "20px",
            fontWeight: "600",
            color: theme.colors.text,
            marginBottom: theme.spacing.lg,
          }}
        >
          Analysis Configuration
        </h3>

        <div
          style={{
            display: "grid",
            gridTemplateColumns: isMobile ? "1fr" : "repeat(2, 1fr)",
            gap: theme.spacing.md,
            marginBottom: theme.spacing.lg,
          }}
        >
          {/* Symbol Input */}
          <div>
            <label
              style={{
                display: "block",
                fontSize: "12px",
                color: theme.colors.textMuted,
                marginBottom: theme.spacing.xs,
                textTransform: "uppercase",
                letterSpacing: "0.5px",
              }}
            >
              Symbol
            </label>
            <input
              type="text"
              value={symbol}
              onChange={(e) => setSymbol(e.target.value.toUpperCase())}
              disabled={isLoading}
              placeholder="SPY"
              style={{
                width: "100%",
                padding: "12px",
                background: "rgba(15, 23, 42, 0.5)",
                border: `1px solid ${theme.colors.border}`,
                borderRadius: theme.borderRadius.md,
                color: theme.colors.text,
                fontSize: "14px",
              }}
            />
          </div>

          {/* Lookback Hours */}
          <div>
            <label
              style={{
                display: "block",
                fontSize: "12px",
                color: theme.colors.textMuted,
                marginBottom: theme.spacing.xs,
                textTransform: "uppercase",
                letterSpacing: "0.5px",
              }}
            >
              Lookback Period: {lookbackHours} hours
            </label>
            <input
              type="range"
              min="6"
              max="168"
              step="6"
              value={lookbackHours}
              onChange={(e) => setLookbackHours(Number(e.target.value))}
              disabled={isLoading}
              style={{
                width: "100%",
                height: "8px",
                borderRadius: "4px",
                outline: "none",
                opacity: isLoading ? 0.5 : 1,
              }}
            />
            <div
              style={{
                fontSize: "11px",
                color: theme.colors.textMuted,
                marginTop: theme.spacing.xs,
              }}
            >
              6 hours - 7 days
            </div>
          </div>
        </div>

        <div
          style={{
            display: "flex",
            gap: theme.spacing.sm,
            alignItems: "center",
            flexWrap: "wrap",
          }}
        >
          <Button
            onClick={analyzeSentiment}
            loading={isLoading}
            disabled={isLoading || !symbol.trim()}
            variant="primary"
            style={{ flex: isMobile ? "1" : "0" }}
          >
            {isLoading ? "Analyzing..." : "Analyze Sentiment"}
          </Button>

          {result && (
            <label
              style={{
                display: "flex",
                alignItems: "center",
                gap: theme.spacing.xs,
                fontSize: "14px",
                color: theme.colors.text,
                cursor: "pointer",
              }}
            >
              <input
                type="checkbox"
                checked={autoRefresh}
                onChange={(e) => setAutoRefresh(e.target.checked)}
                style={{ cursor: "pointer" }}
              />
              <RefreshCw size={16} />
              Auto-refresh (5min)
            </label>
          )}
        </div>
      </Card>

      {/* Results */}
      {result && (
        <>
          {/* Overall Sentiment Card */}
          <Card glow="purple" style={{ marginBottom: theme.spacing.lg }}>
            <div
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
                marginBottom: theme.spacing.lg,
              }}
            >
              <h3
                style={{
                  fontSize: isMobile ? "18px" : "20px",
                  fontWeight: "600",
                  color: theme.colors.text,
                  margin: 0,
                }}
              >
                {getSentimentEmoji(result.overall_sentiment)} Market Sentiment - {result.symbol}
              </h3>
              <div
                style={{
                  fontSize: "12px",
                  color: theme.colors.textMuted,
                }}
              >
                {formatTimestamp(result.timestamp)}
              </div>
            </div>

            <div
              style={{
                display: "grid",
                gridTemplateColumns: isMobile ? "1fr" : "repeat(4, 1fr)",
                gap: theme.spacing.md,
                marginBottom: theme.spacing.lg,
              }}
            >
              {/* Overall Sentiment */}
              <div
                style={{
                  padding: theme.spacing.md,
                  background: `rgba(${
                    result.overall_sentiment === "bullish"
                      ? "16, 185, 129"
                      : result.overall_sentiment === "bearish"
                        ? "239, 68, 68"
                        : "148, 163, 184"
                  }, 0.1)`,
                  border: `2px solid ${getSentimentColor(result.overall_sentiment)}`,
                  borderRadius: theme.borderRadius.md,
                  textAlign: "center",
                }}
              >
                <div style={{ marginBottom: theme.spacing.sm }}>
                  {getSentimentIcon(result.overall_sentiment)}
                </div>
                <div
                  style={{
                    fontSize: "24px",
                    fontWeight: "700",
                    color: getSentimentColor(result.overall_sentiment),
                    textTransform: "uppercase",
                  }}
                >
                  {result.overall_sentiment}
                </div>
                <div
                  style={{
                    fontSize: "12px",
                    color: theme.colors.textMuted,
                    marginTop: theme.spacing.xs,
                  }}
                >
                  {(result.confidence * 100).toFixed(0)}% confidence
                </div>
              </div>

              {/* Sentiment Score */}
              <div
                style={{
                  padding: theme.spacing.md,
                  background: "rgba(139, 92, 246, 0.1)",
                  border: `1px solid ${theme.colors.accent}`,
                  borderRadius: theme.borderRadius.md,
                }}
              >
                <div
                  style={{
                    fontSize: "12px",
                    color: theme.colors.textMuted,
                    marginBottom: theme.spacing.xs,
                  }}
                >
                  SENTIMENT SCORE
                </div>
                <div
                  style={{
                    fontSize: "32px",
                    fontWeight: "700",
                    color: theme.colors.accent,
                  }}
                >
                  {result.sentiment_score >= 0 ? "+" : ""}
                  {result.sentiment_score.toFixed(2)}
                </div>
                <div
                  style={{
                    fontSize: "11px",
                    color: theme.colors.textMuted,
                  }}
                >
                  -1.0 (bearish) to +1.0 (bullish)
                </div>
              </div>

              {/* Article Breakdown */}
              <div
                style={{
                  padding: theme.spacing.md,
                  background: "rgba(6, 182, 212, 0.1)",
                  border: `1px solid ${theme.colors.secondary}`,
                  borderRadius: theme.borderRadius.md,
                }}
              >
                <div
                  style={{
                    fontSize: "12px",
                    color: theme.colors.textMuted,
                    marginBottom: theme.spacing.xs,
                  }}
                >
                  ARTICLES ANALYZED
                </div>
                <div
                  style={{
                    fontSize: "32px",
                    fontWeight: "700",
                    color: theme.colors.secondary,
                  }}
                >
                  {result.total_articles}
                </div>
                <div
                  style={{
                    fontSize: "11px",
                    color: theme.colors.textMuted,
                  }}
                >
                  <span style={{ color: theme.colors.success }}>{result.bullish_count} üöÄ</span> /{" "}
                  <span style={{ color: theme.colors.error }}>{result.bearish_count} üìâ</span> /{" "}
                  <span>{result.neutral_count} ‚û°Ô∏è</span>
                </div>
              </div>

              {/* Impact Score */}
              <div
                style={{
                  padding: theme.spacing.md,
                  background: "rgba(16, 185, 129, 0.1)",
                  border: `1px solid ${theme.colors.success}`,
                  borderRadius: theme.borderRadius.md,
                }}
              >
                <div
                  style={{
                    fontSize: "12px",
                    color: theme.colors.textMuted,
                    marginBottom: theme.spacing.xs,
                  }}
                >
                  AVG IMPACT
                </div>
                <div
                  style={{
                    fontSize: "32px",
                    fontWeight: "700",
                    color: theme.colors.success,
                  }}
                >
                  {(result.avg_impact * 100).toFixed(0)}%
                </div>
                <div
                  style={{
                    fontSize: "11px",
                    color: theme.colors.textMuted,
                  }}
                >
                  Market moving potential
                </div>
              </div>
            </div>

            {/* Top Topics */}
            {result.top_topics.length > 0 && (
              <div>
                <div
                  style={{
                    fontSize: "12px",
                    color: theme.colors.textMuted,
                    marginBottom: theme.spacing.sm,
                    textTransform: "uppercase",
                    letterSpacing: "0.5px",
                  }}
                >
                  <Sparkles size={14} style={{ display: "inline", marginRight: "4px" }} />
                  Trending Topics:
                </div>
                <div
                  style={{
                    display: "flex",
                    gap: theme.spacing.xs,
                    flexWrap: "wrap",
                  }}
                >
                  {result.top_topics.map((topic, idx) => (
                    <div
                      key={idx}
                      style={{
                        padding: "6px 12px",
                        background: "rgba(139, 92, 246, 0.2)",
                        border: `1px solid ${theme.colors.accent}`,
                        borderRadius: theme.borderRadius.sm,
                        fontSize: "13px",
                        color: theme.colors.accent,
                        fontWeight: "600",
                      }}
                    >
                      {topic}
                    </div>
                  ))}
                </div>
              </div>
            )}
          </Card>

          {/* News Articles */}
          <Card glow="green" style={{ marginBottom: theme.spacing.lg }}>
            <h3
              style={{
                fontSize: isMobile ? "18px" : "20px",
                fontWeight: "600",
                color: theme.colors.text,
                marginBottom: theme.spacing.lg,
              }}
            >
              üì∞ Recent News Articles
            </h3>

            {result.articles.length === 0 ? (
              <div
                style={{
                  textAlign: "center",
                  padding: theme.spacing.xl,
                  color: theme.colors.textMuted,
                }}
              >
                No articles found for this symbol in the selected time period.
              </div>
            ) : (
              <div
                style={{
                  display: "grid",
                  gridTemplateColumns: "1fr",
                  gap: theme.spacing.md,
                }}
              >
                {result.articles.slice(0, 10).map((article, idx) => (
                  <a
                    key={idx}
                    href={article.url}
                    target="_blank"
                    rel="noopener noreferrer"
                    style={{
                      padding: theme.spacing.md,
                      background: "rgba(15, 23, 42, 0.5)",
                      border: `1px solid ${getSentimentColor(article.sentiment)}`,
                      borderRadius: theme.borderRadius.md,
                      textDecoration: "none",
                      display: "block",
                      transition: "all 0.2s",
                    }}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.background = "rgba(15, 23, 42, 0.8)";
                      e.currentTarget.style.transform = "translateY(-2px)";
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.background = "rgba(15, 23, 42, 0.5)";
                      e.currentTarget.style.transform = "translateY(0)";
                    }}
                  >
                    <div
                      style={{
                        display: "flex",
                        justifyContent: "space-between",
                        alignItems: "flex-start",
                        marginBottom: theme.spacing.sm,
                      }}
                    >
                      <div
                        style={{
                          flex: 1,
                          fontSize: "16px",
                          fontWeight: "600",
                          color: theme.colors.text,
                          lineHeight: "1.4",
                        }}
                      >
                        {article.title}
                      </div>
                      <div
                        style={{
                          marginLeft: theme.spacing.sm,
                          padding: "4px 12px",
                          background: `rgba(${
                            article.sentiment === "bullish"
                              ? "16, 185, 129"
                              : article.sentiment === "bearish"
                                ? "239, 68, 68"
                                : "148, 163, 184"
                          }, 0.2)`,
                          border: `1px solid ${getSentimentColor(article.sentiment)}`,
                          borderRadius: theme.borderRadius.sm,
                          fontSize: "12px",
                          color: getSentimentColor(article.sentiment),
                          fontWeight: "700",
                          textTransform: "uppercase",
                          whiteSpace: "nowrap",
                        }}
                      >
                        {article.sentiment}
                      </div>
                    </div>

                    <div
                      style={{
                        display: "flex",
                        justifyContent: "space-between",
                        alignItems: "center",
                        fontSize: "12px",
                        color: theme.colors.textMuted,
                      }}
                    >
                      <div>
                        {article.source} ‚Ä¢ {formatTimestamp(article.published_at)}
                      </div>
                      <div>
                        Confidence: {(article.confidence * 100).toFixed(0)}% | Impact:{" "}
                        {(article.impact_score * 100).toFixed(0)}%
                      </div>
                    </div>

                    {article.key_topics.length > 0 && (
                      <div
                        style={{
                          marginTop: theme.spacing.sm,
                          display: "flex",
                          gap: theme.spacing.xs,
                          flexWrap: "wrap",
                        }}
                      >
                        {article.key_topics.slice(0, 3).map((topic, topicIdx) => (
                          <span
                            key={topicIdx}
                            style={{
                              padding: "2px 8px",
                              background: "rgba(139, 92, 246, 0.15)",
                              border: `1px solid ${theme.colors.accent}`,
                              borderRadius: theme.borderRadius.xs,
                              fontSize: "11px",
                              color: theme.colors.accent,
                            }}
                          >
                            {topic}
                          </span>
                        ))}
                      </div>
                    )}
                  </a>
                ))}
              </div>
            )}
          </Card>
        </>
      )}
    </div>
  );
}

"use client";

import {
  Activity,
  AlertTriangle,
  BarChart3,
  Bell,
  BookOpen,
  Brain,
  CheckCircle2,
  Clock,
  CreditCard,
  Database,
  FileText,
  Lock,
  MessageSquare,
  Newspaper,
  Palette,
  Save,
  Settings as SettingsIcon,
  Shield,
  Target,
  ToggleLeft,
  ToggleRight,
  Users,
} from "lucide-react";
import { useCallback, useEffect, useState } from "react";
import toast from "react-hot-toast";
import { useIsMobile } from "../hooks/useBreakpoint";
import { clearUserData, getCurrentUser, getUserAnalytics } from "../lib/userManagement";
import ApprovalQueue from "./ApprovalQueue";
import ClaudeAIChat from "./ClaudeAIChat";
import KillSwitchToggle from "./KillSwitchToggle";
import MLAnalyticsDashboard from "./MLAnalyticsDashboard";
import MLModelManagement from "./MLModelManagement";
import MLTrainingDashboard from "./MLTrainingDashboard";
import PatternBacktestDashboard from "./PatternBacktestDashboard";
import PortfolioOptimizer from "./PortfolioOptimizer";
import RiskDashboard from "./RiskDashboard";
import SchedulerSettings from "./SchedulerSettings";
import SentimentDashboard from "./SentimentDashboard";
import SubscriptionManager from "./SubscriptionManager";
import TradingJournal from "./TradingJournal";
// eslint-disable-next-line @typescript-eslint/no-unused-vars
const _MLTrainingDashboard = MLTrainingDashboard; // Reserved for future ML training feature

interface User {
  id: string;
  email: string;
  name: string;
  role: "owner" | "admin" | "beta" | "alpha" | "user";
  tradingMode: "paper" | "live";
  permissions: {
    canTrade: boolean;
    canBacktest: boolean;
    canViewAnalytics: boolean;
    canModifyStrategies: boolean;
  };
  status: "active" | "suspended";
  createdAt: string;
  lastLogin: string;
}

interface ThemeCustomization {
  primaryColor: string;
  accentColor: string;
  successColor: string;
  errorColor: string;
  warningColor: string;
  infoColor: string;
}

interface TelemetryData {
  sessionId: string;
  userId: string;
  action: string;
  component: string;
  timestamp: string;
  metadata: Record<string, unknown>;
}

interface SettingsData {
  defaultExecutionMode: "requires_approval" | "autopilot";
  enableSMSAlerts: boolean;
  enableEmailAlerts: boolean;
  enablePushNotifications: boolean;
  enablePerformanceTracking: boolean;
  minTradesForPerformanceData: number;
  defaultSlippageBudget: number;
  defaultMaxReprices: number;
}

interface SettingsProps {
  isOpen: boolean;
  onClose: () => void;
}

export default function Settings({ isOpen, onClose }: SettingsProps) {
  const isMobile = useIsMobile();
  const currentUserData = getCurrentUser();
  const [currentUser] = useState({
    id: currentUserData?.userId || "owner-001",
    role: "owner" as const,
  });
  const isOwner = currentUser.role === "owner";
  const isAdmin = currentUser.role === "owner" || currentUser.role === "admin";

  const [activeTab, setActiveTab] = useState<
    | "personal"
    | "users"
    | "theme"
    | "permissions"
    | "telemetry"
    | "trading"
    | "journal"
    | "risk"
    | "automation"
    | "approvals"
  >("personal");

  const [settings, setSettings] = useState<SettingsData>({
    defaultExecutionMode: "requires_approval",
    enableSMSAlerts: true,
    enableEmailAlerts: true,
    enablePushNotifications: false,
    enablePerformanceTracking: true,
    minTradesForPerformanceData: 10,
    defaultSlippageBudget: 0.4,
    defaultMaxReprices: 4,
  });

  const [users, setUsers] = useState<User[]>([]);
  const [themeCustom, setThemeCustom] = useState<ThemeCustomization>({
    primaryColor: "#10b981",
    accentColor: "#7E57C2",
    successColor: "#10b981",
    errorColor: "#ef4444",
    warningColor: "#f59e0b",
    infoColor: "#14b8a6",
  });
  const [telemetryEnabled, setTelemetryEnabled] = useState(true);
  const [telemetryData, setTelemetryData] = useState<TelemetryData[]>([]);

  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [saveMessage, setSaveMessage] = useState("");

  // Risk Tolerance State
  const [riskTolerance, setRiskTolerance] = useState<number>(50);
  const [riskLimits, setRiskLimits] = useState<{
    risk_category: string;
    max_position_size_percent: number;
    max_positions: number;
    description: string;
  } | null>(null);
  const [isLoadingRisk, setIsLoadingRisk] = useState(false);

  // Paper Trading Account Balance State
  const [paperAccountBalance, setPaperAccountBalance] = useState<number>(100000);
  const [accountInfo, setAccountInfo] = useState<{
    equity: number;
    cash: number;
    buying_power: number;
  } | null>(null);
  const [isLoadingBalance, setIsLoadingBalance] = useState(false);

  useEffect(() => {
    if (!isOpen) return;

    const savedSettings = localStorage.getItem("allessandra_settings");
    if (savedSettings) {
      try {
        setSettings(JSON.parse(savedSettings));
      } catch (error) {
        console.error("Failed to load settings:", error);
      }
    }

    // Fetch risk tolerance and account balance from backend
    fetchRiskTolerance();
    fetchAccountBalance();

    if (isAdmin) {
      loadMockUsers();
      loadTelemetryData();
    }
  }, [isOpen, isAdmin]);

  const loadMockUsers = () => {
    setUsers([
      {
        id: "owner-001",
        email: "owner@paid.com",
        name: "System Owner",
        role: "owner",
        tradingMode: "paper",
        permissions: {
          canTrade: true,
          canBacktest: true,
          canViewAnalytics: true,
          canModifyStrategies: true,
        },
        status: "active",
        createdAt: "2025-01-01",
        lastLogin: new Date().toISOString().split("T")[0],
      },
      {
        id: "beta-001",
        email: "beta.tester1@paid.com",
        name: "Beta Tester 1",
        role: "beta",
        tradingMode: "paper",
        permissions: {
          canTrade: true,
          canBacktest: true,
          canViewAnalytics: true,
          canModifyStrategies: false,
        },
        status: "active",
        createdAt: "2025-09-15",
        lastLogin: "2025-10-05",
      },
    ]);
  };

  const loadTelemetryData = () => {
    const mockData: TelemetryData[] = [
      {
        sessionId: "sess-001",
        userId: "beta-001",
        action: "execute_trade",
        component: "ExecuteTradeForm",
        timestamp: new Date().toISOString(),
        metadata: { symbol: "AAPL", side: "buy", quantity: 10 },
      },
    ];
    setTelemetryData(mockData);
  };

  // Fetch Risk Tolerance from Backend
  const fetchRiskTolerance = async () => {
    try {
      const apiToken = process.env.NEXT_PUBLIC_API_TOKEN;
      const baseUrl = process.env.NEXT_PUBLIC_BACKEND_API_BASE_URL || "/api/proxy/api";

      const response = await fetch(`${baseUrl}/users/preferences`, {
        headers: {
          Authorization: `Bearer ${apiToken}`,
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        const data = await response.json();
        setRiskTolerance(data.risk_tolerance || 50);

        // Fetch risk limits
        const limitsResponse = await fetch(`${baseUrl}/users/risk-limits`, {
          headers: {
            Authorization: `Bearer ${apiToken}`,
            "Content-Type": "application/json",
          },
        });

        if (limitsResponse.ok) {
          const limitsData = await limitsResponse.json();
          setRiskLimits(limitsData);
        }
      }
    } catch (error) {
      console.error("Failed to fetch risk tolerance:", error);
    }
  };

  // Fetch Account Balance from Backend
  const fetchAccountBalance = async () => {
    try {
      const apiToken = process.env.NEXT_PUBLIC_API_TOKEN;
      const baseUrl = process.env.NEXT_PUBLIC_BACKEND_API_BASE_URL || "/api/proxy/api";

      const response = await fetch(`${baseUrl}/account`, {
        headers: {
          Authorization: `Bearer ${apiToken}`,
          "Content-Type": "application/json",
        },
      });

      if (response.ok) {
        const data = await response.json();
        setAccountInfo({
          equity: parseFloat(data.equity || data.portfolio_value || "0"),
          cash: parseFloat(data.cash || "0"),
          buying_power: parseFloat(data.buying_power || "0"),
        });
        setPaperAccountBalance(parseFloat(data.equity || data.portfolio_value || "100000"));
      }
    } catch (error) {
      console.error("Failed to fetch account balance:", error);
    }
  };

  // Update Risk Tolerance with Debounce
  const updateRiskTolerance = useCallback(async (newValue: number) => {
    setIsLoadingRisk(true);

    try {
      const apiToken = process.env.NEXT_PUBLIC_API_TOKEN;
      const baseUrl = process.env.NEXT_PUBLIC_BACKEND_API_BASE_URL || "/api/proxy/api";

      const response = await fetch(`${baseUrl}/users/preferences`, {
        method: "PATCH",
        headers: {
          Authorization: `Bearer ${apiToken}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ risk_tolerance: newValue }),
      });

      if (response.ok) {
        const data = await response.json();
        setRiskTolerance(data.risk_tolerance);

        // Refresh risk limits
        const limitsResponse = await fetch(`${baseUrl}/users/risk-limits`, {
          headers: {
            Authorization: `Bearer ${apiToken}`,
            "Content-Type": "application/json",
          },
        });

        let limitsData = null;
        if (limitsResponse.ok) {
          limitsData = await limitsResponse.json();
          setRiskLimits(limitsData);
        }

        const riskCategory =
          limitsData?.risk_category ||
          (newValue <= 33 ? "Conservative" : newValue <= 66 ? "Moderate" : "Aggressive");
        toast.success(`Risk tolerance updated to ${newValue}% (${riskCategory})`);
      } else {
        toast.error("Failed to update risk tolerance");
      }
    } catch (error) {
      console.error("Failed to update risk tolerance:", error);
      toast.error("Failed to update risk tolerance");
    } finally {
      setIsLoadingRisk(false);
    }
  }, []);

  // Update Paper Account Balance
  const updatePaperAccountBalance = async () => {
    setIsLoadingBalance(true);

    try {
      const apiToken = process.env.NEXT_PUBLIC_API_TOKEN;
      const baseUrl = process.env.NEXT_PUBLIC_BACKEND_API_BASE_URL || "/api/proxy/api";

      const response = await fetch(`${baseUrl}/users/preferences`, {
        method: "PATCH",
        headers: {
          Authorization: `Bearer ${apiToken}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ paper_account_balance: paperAccountBalance }),
      });

      if (response.ok) {
        toast.success(`Paper account balance updated to $${paperAccountBalance.toLocaleString()}`);
        // Refresh account info after update
        await fetchAccountBalance();
      } else {
        toast.error("Failed to update paper account balance");
      }
    } catch (error) {
      console.error("Failed to update paper account balance:", error);
      toast.error("Failed to update paper account balance");
    } finally {
      setIsLoadingBalance(false);
    }
  };

  const updateSetting = <K extends keyof SettingsData>(key: K, value: SettingsData[K]) => {
    setSettings((prev) => ({ ...prev, [key]: value }));
    setHasUnsavedChanges(true);
  };

  const handleSaveSettings = async () => {
    setIsSaving(true);
    setSaveMessage("");

    localStorage.setItem("allessandra_settings", JSON.stringify(settings));

    await new Promise((resolve) => setTimeout(resolve, 1500));

    setIsSaving(false);
    setHasUnsavedChanges(false);
    setSaveMessage("Settings saved successfully!");

    setTimeout(() => {
      setSaveMessage("");
    }, 3000);
  };

  const handleReset = () => {
    const defaultSettings: SettingsData = {
      defaultExecutionMode: "requires_approval",
      enableSMSAlerts: true,
      enableEmailAlerts: true,
      enablePushNotifications: false,
      enablePerformanceTracking: true,
      minTradesForPerformanceData: 10,
      defaultSlippageBudget: 0.4,
      defaultMaxReprices: 4,
    };
    setSettings(defaultSettings);
    setHasUnsavedChanges(true);
  };

  const toggleUserStatus = (userId: string) => {
    if (!isOwner) return;
    setUsers(
      users.map((user) =>
        user.id === userId
          ? { ...user, status: user.status === "active" ? "suspended" : "active" }
          : user
      )
    );
    setHasUnsavedChanges(true);
  };

  const updateUserPermission = (
    userId: string,
    permission: keyof User["permissions"],
    value: boolean
  ) => {
    if (!isOwner) return;
    setUsers(
      users.map((user) =>
        user.id === userId
          ? { ...user, permissions: { ...user.permissions, [permission]: value } }
          : user
      )
    );
    setHasUnsavedChanges(true);
  };

  const toggleTradingMode = (userId: string) => {
    if (!isOwner && userId !== currentUser.id) return;
    setUsers(
      users.map((user) =>
        user.id === userId
          ? { ...user, tradingMode: user.tradingMode === "paper" ? "live" : "paper" }
          : user
      )
    );
    setHasUnsavedChanges(true);
  };

  const exportTelemetryReport = () => {
    const dataStr = JSON.stringify(telemetryData, null, 2);
    const dataBlob = new Blob([dataStr], { type: "application/json" });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement("a");
    link.href = url;
    link.download = `telemetry-${new Date().toISOString()}.json`;
    link.click();
  };

  if (!isOpen) return null;

  const analytics = currentUserData ? getUserAnalytics() : null;

  const tabs = [
    { id: "personal", label: "Personal Settings", icon: SettingsIcon, alwaysShow: true },
    { id: "subscription", label: "Subscription & Billing", icon: CreditCard, alwaysShow: true },
    { id: "journal", label: "Trading Journal", icon: BookOpen, alwaysShow: true },
    { id: "risk", label: "Risk Control", icon: Shield, alwaysShow: true },
    { id: "ml-training", label: "ML Training", icon: Brain, alwaysShow: true },
    { id: "pattern-backtest", label: "Pattern Backtest", icon: BarChart3, alwaysShow: true },
    { id: "ml-models", label: "ML Models", icon: Database, alwaysShow: true },
    { id: "ml-analytics", label: "ML Analytics", icon: Activity, alwaysShow: true },
    { id: "portfolio-optimizer", label: "Portfolio Optimizer", icon: Target, alwaysShow: true },
    { id: "sentiment", label: "News Sentiment", icon: Newspaper, alwaysShow: true },
    { id: "ai-chat", label: "AI Chat", icon: MessageSquare, alwaysShow: true },
    { id: "automation", label: "Automation", icon: Clock, alwaysShow: true },
    { id: "approvals", label: "Approvals", icon: CheckCircle2, alwaysShow: true },
    { id: "users", label: "User Management", icon: Users, adminOnly: true },
    { id: "theme", label: "Theme", icon: Palette, adminOnly: true },
    { id: "permissions", label: "Permissions", icon: Lock, adminOnly: true },
    { id: "telemetry", label: "Telemetry", icon: Database, adminOnly: true },
    { id: "trading", label: "Trading Control", icon: Activity, adminOnly: true },
  ].filter((tab) => tab.alwaysShow || (tab.adminOnly && isAdmin));

  return (
    <div
      style={{
        position: "fixed",
        inset: 0,
        zIndex: 50,
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        padding: "16px",
        background: "rgba(0, 0, 0, 0.7)",
        backdropFilter: "blur(10px)",
        overflowY: "auto",
        color: "#e2e8f0",
      }}
    >
      <div
        style={{
          background: "rgba(30, 41, 59, 0.8)",
          backdropFilter: "blur(20px)",
          border: "1px solid rgba(16, 185, 129, 0.3)",
          borderRadius: "20px",
          boxShadow: "0 0 40px rgba(16, 185, 129, 0.15)",
          maxWidth: isMobile ? "95vw" : "1200px",
          width: "100%",
          maxHeight: "90vh",
          overflow: "hidden",
          display: "flex",
          flexDirection: "column",
          margin: isMobile ? "16px 0" : "32px 0",
        }}
      >
