/**
 * Playwright Global Setup
 * Runs once before all tests to ensure clean test environment
 * Version: 1.0.0
 */

import { execSync } from "child_process";
import { existsSync, readdirSync, unlinkSync } from "fs";
import { join } from "path";

/**
 * Kill processes on specific ports (cross-platform)
 */
function killPort(port: number): void {
  console.log(`[Global Setup] Cleaning up port ${port}...`);

  try {
    if (process.platform === "win32") {
      // Windows
      const cmd = `powershell -Command "Get-NetTCPConnection -LocalPort ${port} -State Listen -ErrorAction SilentlyContinue | ForEach-Object { Stop-Process -Id $_.OwningProcess -Force -ErrorAction SilentlyContinue }"`;
      execSync(cmd, { stdio: "ignore" });
    } else {
      // Unix/Linux/Mac
      execSync(`lsof -ti:${port} | xargs kill -9 2>/dev/null || true`, {
        stdio: "ignore",
      });
    }
    console.log(`  Port ${port} cleared`);
  } catch (error) {
    // Ignore errors if port wasn't in use
    console.log(`  Port ${port} was not in use`);
  }
}

/**
 * Clean up PID files from previous test runs
 */
function cleanupPidFiles(): void {
  console.log("[Global Setup] Cleaning up PID files...");

  const pidDirs = [join(__dirname, "..", "..", "backend", ".run"), join(__dirname, "..", ".run")];

  let cleaned = 0;

  for (const pidDir of pidDirs) {
    if (!existsSync(pidDir)) {
      continue;
    }

    try {
      const files = readdirSync(pidDir);
      for (const file of files) {
        if (file.endsWith(".pid")) {
          const pidFile = join(pidDir, file);
          unlinkSync(pidFile);
          cleaned++;
        }
      }
    } catch (error) {
      console.warn(`  Warning: Could not clean PID directory ${pidDir}`);
    }
  }

  console.log(`  Cleaned up ${cleaned} PID file(s)`);
}

/**
 * Validate environment configuration
 */
function validateEnvironment(): void {
  console.log("[Global Setup] Validating environment...");

  // Check critical environment variables
  const requiredVars = ["NEXT_PUBLIC_BACKEND_API_BASE_URL", "NEXT_PUBLIC_API_TOKEN"];

  const missing: string[] = [];

  for (const varName of requiredVars) {
    if (!process.env[varName]) {
      missing.push(varName);
    }
  }

  if (missing.length > 0) {
    console.warn(`  Warning: Missing environment variables: ${missing.join(", ")}`);
    console.warn("  Tests may fail without proper configuration");
  } else {
    console.log("  Environment configuration valid");
  }
}

/**
 * Main global setup function
 */
async function globalSetup(): Promise<void> {
  console.log("=".repeat(70));
  console.log("Playwright Global Setup - Pre-Test Cleanup");
  console.log("=".repeat(70));

  // 1. Clean up test ports (3000, 3001, 3002, 3003, 8000, 8001, 8002)
  const testPorts = [3000, 3001, 3002, 3003, 8000, 8001, 8002];
  for (const port of testPorts) {
    killPort(port);
  }

  // Wait for ports to fully release
  await new Promise((resolve) => setTimeout(resolve, 2000));

  // 2. Clean up PID files
  cleanupPidFiles();

  // 3. Validate environment
  validateEnvironment();

  console.log("=".repeat(70));
  console.log("Global Setup Complete - Test environment is clean");
  console.log("=".repeat(70));
  console.log("");
}

export default globalSetup;

/**
 * Playwright Global Teardown
 * Runs once after all tests to cleanup test servers and artifacts
 * Version: 1.0.0
 */

import { execSync } from "child_process";
import { existsSync, readdirSync, unlinkSync } from "fs";
import { join } from "path";

/**
 * Stop all test servers gracefully
 */
function stopTestServers(): void {
  console.log("[Global Teardown] Stopping test servers...");

  const testPorts = [3000, 8002];

  for (const port of testPorts) {
    try {
      if (process.platform === "win32") {
        execSync(
          `powershell -Command "Get-NetTCPConnection -LocalPort ${port} -State Listen -ErrorAction SilentlyContinue | ForEach-Object { Stop-Process -Id $_.OwningProcess -Force -ErrorAction SilentlyContinue }"`,
          { stdio: "ignore" }
        );
      } else {
        execSync(`lsof -ti:${port} | xargs kill -TERM 2>/dev/null || true`, {
          stdio: "ignore",
        });
      }
      console.log(`  Stopped server on port ${port}`);
    } catch (error) {
      // Ignore if already stopped
    }
  }
}

/**
 * Remove all PID files
 */
function removePidFiles(): void {
  console.log("[Global Teardown] Removing PID files...");

  const pidDirs = [join(__dirname, "..", "..", "backend", ".run"), join(__dirname, "..", ".run")];

  let removed = 0;

  for (const pidDir of pidDirs) {
    if (!existsSync(pidDir)) continue;

    try {
      const files = readdirSync(pidDir);
      for (const file of files) {
        if (file.endsWith(".pid")) {
          unlinkSync(join(pidDir, file));
          removed++;
        }
      }
    } catch (error) {
      console.warn(`  Could not clean ${pidDir}`);
    }
  }

  console.log(`  Removed ${removed} PID file(s)`);
}

/**
 * Archive test artifacts (optional)
 */
function archiveTestArtifacts(): void {
  console.log("[Global Teardown] Test artifacts available:");

  const artifactDirs = [
    { path: join(__dirname, "..", "test-results"), name: "Test Results" },
    { path: join(__dirname, "..", "playwright-report"), name: "HTML Report" },
  ];

  for (const { path, name } of artifactDirs) {
    if (existsSync(path)) {
      console.log(`  ${name}: ${path}`);
    }
  }
}

/**
 * Main global teardown function
 */
async function globalTeardown(): Promise<void> {
  console.log("=".repeat(70));
  console.log("Playwright Global Teardown - Post-Test Cleanup");
  console.log("=".repeat(70));

  // 1. Stop test servers gracefully
  stopTestServers();

  // Wait for servers to shutdown
  await new Promise((resolve) => setTimeout(resolve, 2000));

  // 2. Remove PID files
  removePidFiles();

  // 3. Archive/report artifacts
  archiveTestArtifacts();

  console.log("=".repeat(70));
  console.log("Global Teardown Complete - All test resources cleaned up");
  console.log("=".repeat(70));
  console.log("");
}

